mapper.select:
SELECT count(l.id) FROM t_pro_ledger l,t_pro_ledger_master m WHERE m.stat = 1 AND m.id = l.master_id AND m.source_id=#{ledgerDto.sourceId}
AND m.action_date >= #{ledgerDto.actionDate}
AND m.action_date <= #{ledgerDto.nextDate}
AND m.receiver_name like "%"#{ledgerDto.receiverName}"%"

SELECT DISTINCT m.id id, m.ware_batch_no wareBatchNo, m.action_date sendDate, m.receiver_name receiverName, u.`name` userName FROM t_pro_ledger l,t_pro_ledger_master m,t_pro_users u WHERE m.user_id=u.id AND m.stat = 1 AND m.source_id =#{ledgerDto.sourceId}
AND m.action_date >= #{ledgerDto.actionDate}
AND m.action_date <= #{ledgerDto.nextDate}
AND m.receiver_name like "%"#{ledgerDto.receiverName}"%"
