mapper.select:
SELECT
count(m.id)
FROM
t_pro_ledger_master m
WHERE
m.stat = 1
AND m.source_id=#{ledgerDto.sourceId}

	AND m.action_date >= #{ledgerDto.actionDate}


	AND m.action_date <= #{ledgerDto.nextDate}


	AND m.receiver_name like "%"#{ledgerDto.receiverName}"%"

SELECT a.id,b.id as masterId,b.action_date as actionDate,b.receiver_id as receiverId,b.receiver_name as receiverName,b.user_id as userId,b.source_id as sourceId,b.ware_batch_no as wareBatchNo,
b.haul_status as haulStatus,a.wares_id as waresId,a.name,a.spce,a.supplier_id as supplierId,a.supplier_code as supplierCode, a.supplier_name as supplierName, a.quantity,a.production_date as productionDate,
a.production_name as productionName,a.batch_no as batchNo,a.trace_code as traceCode,a.create_time as createTime,a.last_update_time as lastUpdateTime,a.stat
FROM t_pro_ledger a
LEFT JOIN t_pro_ledger_master b on a.master_id = b.id
WHERE b.stat=1


	AND b.receiver_id = #{ledgerDto.receiverId}


	AND b.id = #{ledgerDto.masterId}


	AND b.receiver_name like "%"#{ledgerDto.receiverName}"%"


	AND b.ware_batch_no = #{ledgerDto.wareBatchNo}

SELECT a.id,b.id as masterId,b.action_date as actionDate,b.receiver_id as receiverId,b.receiver_name as receiverName,b.user_id as userId,b.source_id as sourceId,b.ware_batch_no as wareBatchNo, b.haul_status as haulStatus,a.wares_id as waresId,a.name,a.spce,a.supplier_id as supplierId,a.supplier_code as supplierCode, c.supplier_name as supplierName, a.quantity,a.production_date as productionDate, a.production_name as productionName,a.batch_no as batchNo,a.trace_code as traceCode,a.create_time as createTime,a.last_update_time as lastUpdateTime,a.stat FROM t_pro_ledger a LEFT JOIN t_pro_ledger_master b on a.master_id = b.id LEFT JOIN t_pro_supplier c on b.source_id = c.id WHERE b.stat=1
AND b.receiver_id = #{ledgerDto.receiverId}
AND b.id = #{ledgerDto.masterId}
AND b.source_id = #{ledgerDto.sourceId}
AND b.receiver_name like "%"#{ledgerDto.receiverName}"%"
GROUP BY b.ware_batch_no ORDER BY a.create_time desc limit #{page.startNum}, #{page.pageSize}

SELECT count(*) from  (SELECT b.id, COUNT(*) counts FROM t_pro_ledger a
LEFT JOIN t_pro_ledger_master b on a.master_id = b.id
LEFT JOIN t_pro_supplier c on b.source_id = c.id
WHERE  b.stat=1

	AND b.receiver_id = #{ledgerDto.receiverId}


	AND b.id = #{ledgerDto.masterId}


	AND b.source_id = #{ledgerDto.sourceId}


	AND b.receiver_name like "%"#{ledgerDto.receiverName}"%"

GROUP BY b.ware_batch_no) t

SELECT DISTINCT m.id masterId, m.ware_batch_no wareBatchNo, m.action_date sendDate, m.receiver_name receiverName, m.haul_status haulStatus, (SELECT name FROM t_pro_users WHERE id=m.user_id) userName FROM t_pro_ledger l,t_pro_ledger_master m WHERE m.stat = 1 AND m.source_id =#{ledgerDto.sourceId}
AND m.action_date >= #{ledgerDto.actionDate}
AND m.action_date <= #{ledgerDto.nextDate}
AND m.receiver_name like "%"#{ledgerDto.receiverName}"%"
ORDER BY m.action_date DESC , m.create_time DESC
limit #{ph.beginRow},#{ph.rows}
